mapper.select:
select count(userId)
from members
where userId = #{userId}

select userId, userName, userPwd, userEmail, userPhone, userRole, userDel, token
from members
where userId = #{userId}

mapper.update:
update members

	userName = #{userName},
	userPwd = md5(#{userPwd}),
	userEmail= #{userEmail},
	userPhone = #{userPhone}

where userId = #{userId}

mapper.select:
select token
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token}
where userId = #{userId}

update members
set token = #{token, jdbcType=VARCHAR}
where userId = #{userId}
